#!/usr/bin/env bash#Determine directory script is running in...#https://stackoverflow.com/questions/59895/how-do-i-get-the-directory-where-a-bash-script-is-located-from-within-the-scriptget_script_dir(){	#This version is supposed to resolve symlinks	SRC=${BASH_SOURCE[0]}	while [ -L "$SRC" ]; do		MY_DIR=$( cd -P "$( dirname "$SRC" )" >/dev/null 2>&1 && pwd )		SRC=$(readlink "$SRC")		[[ $SRC != /* ]] && SRC=$MY_DIR/$SRC	done	MY_DIR=$( cd -P "$( dirname "$SRC" )" >/dev/null 2>&1 && pwd )}get_script_dirMLAPP_PATH="${MY_DIR}/src/gui/TrueSpotMinimal.mlapp"matlab -nosplash -sd "${${MY_DIR}/src}" -r "${MLAPP_PATH};"